internal class DayOne : IPuzzle
{
    public void Test()
    {
        TestPart1();
        TestPart2();
    }

    public void RunPuzzle()
    {
        RunPuzzlePart1();
        RunPuzzlePart2();
    }

    private static int Move(char sign)
    {
        return sign switch
        {
            '(' => 1,
            ')' => -1,
            _ => throw new ArgumentException($"Unrecognized input {sign}")
        };
    }

    private static int GetFloor(ReadOnlySpan<char> sequence)
    {
        int floor = 0;
        foreach (char item in sequence)
        {
            floor += Move(item);
        }

        return floor;
    }

    private static (bool foundBasement, int charPosition) GetBasementPosition(string sequence)
    {
        int floor = 0;
        int i = 0;
        bool foundBasement = false;
        foreach (char item in sequence)
        {
            i++;
            floor += Move(item);
            if (floor == -1)
            {
                foundBasement = true;
                break;
            }
        }

        return (foundBasement, i);
    }


    private static (string testCase, int expectedOutput)[] GetTestCasesPart1()
    {
        return [("(())", 0), ("()()", 0), ("(((", 3), ("(()(()(", 3), ("))(((((", 3), ("())", -1), ("))(", -1), (")))", -3), (")())())", -3)];
    }

    internal static void TestPart1()
    {
        Console.WriteLine("Running test for Puzzle Part 1");
        foreach ((string testCase, int expectedOutput) in GetTestCasesPart1())
        {
            int finalFloor = GetFloor(testCase);
            if (finalFloor != expectedOutput)
            {
                throw new Exception($"Expected a final floor number of {expectedOutput} but got {finalFloor}");
            }

            Console.WriteLine($"    Arrived at: floor {finalFloor}");
        }
    }

    internal static void RunPuzzlePart1()
    {
        Console.WriteLine($"Answer for Puzzle Part 1: {GetFloor(_puzzleInput)}");
    }

    private static (string testCase, int expectedOutput)[] GetTestCasesPart2()
    {
        return [("(", 1), ("()())", 5)];
    }

    internal static void TestPart2()
    {
        Console.WriteLine("Running test for Puzzle Part 2");
        foreach ((string testCase, int expectedOutput) in GetTestCasesPart2())
        {
            (bool foundBasement, int charPosition) = GetBasementPosition(testCase);
            if (charPosition != expectedOutput)
            {
                throw new Exception($"Expected to reach basement at character {expectedOutput} but got {charPosition}");
            }

            Console.WriteLine($"    - Arrived at basement at character: floor {charPosition}");
        }
    }

    internal static void RunPuzzlePart2()
    {
        Console.WriteLine($"Answer for Puzzle Part 2: {GetBasementPosition(_puzzleInput)}");
    }

